//全局监听点击事件，用于隐藏空间弹出框和用户信息弹出框
document.addEventListener("click", function (e) {
    var extraSpace = document.getElementsByClassName("extra-space");
  var collectExtraSpace = document.getElementsByClassName("extra-collect");
    var collectBtn = document.getElementById("btn-page-collect");
    var extraSpaceBtn = document.getElementsByClassName("extra-space-btn");
    if(collectBtn&&extraSpace&&collectExtraSpace&&collectExtraSpace.length>0&& extraSpace.length>0 && extraSpaceBtn && extraSpaceBtn.length>0) {
      if(!collectBtn.contains(e.target)&&!collectExtraSpace[0].contains(e.target)&&!extraSpace[0].contains(e.target) && !extraSpaceBtn[0].contains(e.target)) {
        cancelMenuPop();
      }
    }

    var userSpace = document.getElementsByClassName("user-space");
    var userAvatar = document.getElementsByClassName("navbar-avatar");
    if(userSpace && userSpace.length>0 && userAvatar && userAvatar.length>0) {
      if(!userSpace[0].contains(e.target) && !userAvatar[0].contains(e.target)) {
        userSpace[0].style.display = "none";
      }
    }
  
    var shareModalContainer= document.getElementById("share-modal-container");
    var shareContent= document.getElementById("share-content");
    var share = document.getElementById("page-share");
    if(shareContent && share) {
      if(!shareContent.contains(e.target) && !share.contains(e.target)) {
        shareModalContainer.style.display = "none";
      }
    }
  
    var renameModalContainer= document.getElementById("rename-modal-container");
    var renameContent= document.getElementById("rename-content");
    var spaceRename = document.getElementById("spaceRename");
    if(renameContent && spaceRename) {
      if(!renameContent.contains(e.target) && !spaceRename.contains(e.target)) {
        renameModalContainer.style.display = "none";
      }
    }
  
   var ckeIssueSelTypeSpace = document.getElementById("sel-option");
    var ckeIssueSelTypeBtn = document.getElementById("sel-wrap");
    if(ckeIssueSelTypeSpace && ckeIssueSelTypeBtn ) {
      if(!ckeIssueSelTypeSpace.contains(e.target) && !ckeIssueSelTypeBtn.contains(e.target)) {
        ckeIssueSelTypeSpace.style.display = "none";
      }
    }
})

//空间按钮点击事件
function application() {
    var body = document.getElementById("body");
    var application = document.getElementsByClassName("extra-space");
    var spaceShadow = document.getElementsByClassName("space-shadow");
    if (application[0].style.display == "block") {
        application[0].style.display = "none";
        spaceShadow[0].removeClassName("space-shadow-add");
    } else {
        application[0].style.display = "block";
        spaceShadow[0].addClassName("space-shadow-add");
    }
    body.addClassName("space-display");
}

//隐藏空间弹出框
function cancelMenuPop() {
    var body = document.getElementById("body");
    var application = document.getElementsByClassName("extra-space");
    var collect = document.getElementsByClassName("extra-collect");
    var spaceShadow = document.getElementsByClassName("space-shadow");
  var collectSpaceShadow = document.getElementsByClassName("collect-shadow");
    application[0].style.display = "none";
    collect[0].style.display = "none";
    spaceShadow[0].removeClassName("space-shadow-add");
    collectSpaceShadow[0].removeClassName("collect-shadow-add");
    body.removeClassName("space-display");
    body.removeClassName("collect-display");
}

