if (a[0] != null) {
 a[0].onclick=function(){
   return false;
 }
}

var user = avatar[0].getElementsByTagName("img");
if(user[0] != null){
  user[0].onclick = function () {
      var userInfo = document.getElementsByClassName("user-space");
      if (userInfo[0].style.display == "block") {
        userInfo[0].style.display = "none";
      } else {
        userInfo[0].style.display = "block";
      }
  }
}
}
}
function userInfo() {
    var userInfo = document.getElementsByClassName("user-space");
    if (userInfo[0] != null) {
      if (userInfo[0].style.display == "block") {
         userInfo[0].style.display = "none";
      } else {
         userInfo[0].style.display = "block";
      }
   }
}

