//左侧边栏滚动
var leftPanels =document.getElementById("leftPanels");
if(leftPanels) {
  leftPanels.style.height = (window.innerHeight - 48)+'px';
  leftPanels.style.overflow = 'auto';
  leftPanels.style.top='48px';
}


window.addEventListener('scroll',winScroll);

function winScroll(e){
  if(leftPanels) {
    if(window.scrollY >= 48){
      leftPanels.style.top='0';
      leftPanels.style.height = window.innerHeight+'px';
       //     leftPanels.style.overflow = 'auto';
    }else if(window.scrollY == 0){
      leftPanels.style.top='48px';
      leftPanels.style.height = (window.innerHeight - 48)+'px';
      //leftPanels.style.overflow = 'hidden';
    }else if(window.scrollY >0 && window.scrollY < 48){
      leftPanels.style.top=48-window.scrollY+'px';
      leftPanels.style.height = (window.innerHeight - 48+window.scrollY)+'px';
      //leftPanels.style.overflow = 'auto';
    }
  }
}

