//第二步
function GetCurrentUrlPath() {
    var url = document.location.toString();
    var arrUrl = url.split("//");
    var prefixUrl = "";
    var paramUrl = "";

    var start = arrUrl[1].indexOf("/");
    var relUrl = arrUrl[1].substring(start);//stop省略，截取从start开始到结尾的所有字符

    if (relUrl.indexOf("?") != -1) {
        prefixUrl = relUrl.split("?")[0];
        paramUrl = relUrl.split("?")[1];
    }else{
        prefixUrl = relUrl;
    }
    var arr = new Array();
    arr[0] = prefixUrl;
    arr[1] = paramUrl;
    return arr;
}

var urlArr = GetCurrentUrlPath();
var reSecond = /\/bin\/edit(\/[\s\S]+)+\/Code\/[\s\S]+Class/;
if(urlArr[1].startsWith("wizard=true") && reSecond.test(urlArr[0])){
  var thisbody = document.getElementById("body");
  thisbody.addClassName("createAppSecondStep");
  changeTitle();  //修改title
  changeNext(); //修改英文下一步为中文
  changePrevious();  //修改上一步为中文
  //addTitle(); //添加select page
}

window.onload = function(){
  if(urlArr[1].startsWith("wizard=true") && reSecond.test(urlArr[0])){
    setLines();  //添加横线修改名称
  }
}

function changeTitle(){
  var doc = document.getElementById("document-title");
  var elements = doc.getElementsByTagName("h1");
  elements[0].innerHTML = "创建空间流程";
}

function setLines(){
  var line = `
    <div class="line">
    </div>
  `;
  var steps = document.getElementsByClassName("steps");
  if(steps!=undefined && steps!=null){
    var lis = steps[0].getElementsByTagName("li");
    for(var i=0;i<3;i++){
      lis[i].insertAdjacentHTML('afterEnd', line);
    }
    var name0=lis[0].getElementsByClassName("name");
    name0[0].innerHTML="Name your application";
    var name1=lis[1].getElementsByClassName("name");
    name1[0].innerHTML="Structure your data";
    var name2=lis[2].getElementsByClassName("name");
    name2[0].innerHTML="Locate your data";
    var name3=lis[3].getElementsByClassName("name");
    name3[0].innerHTML="Customize your application home page"; 
  }
}

function changeNext(){
  var nextStep = document.getElementById("wizard-next");
  if(nextStep!=undefined && nextStep!=null){
    nextStep.value="下一步";
  }
}

function changePrevious(){
  var content = document.getElementById("mainContentArea");
  var previousStep = content.getElementsByClassName("secondary");
  if(previousStep[0]!=undefined){
    previousStep[0].innerHTML = "上一步";
  }
}

