}

function addLocationWord(){
  var words = `
    <font class="addLoc">Add Location</font>
  `;
  var area = document.getElementsByClassName("col-xs-12 col-md-6");
  for(var j=0;j<area.length;j++){
      var addButtons = area[j].getElementsByClassName("fa fa-plus");
      for(var i=0;i<addButtons.length;i++){
        addButtons[i].insertAdjacentHTML('beforeEnd', words);
      }
  }
}

