function addLiveTableClass(){
  var formbody = document.getElementsByClassName("form-body");
  var dls = formbody[0].getElementsByTagName("dl");
  var dds = dls[0].getElementsByTagName("dd");
  var dts = dls[0].getElementsByTagName("dt");
  dds[1].addClassName("livetablehidden");
  dts[1].addClassName("livetablehidden");
}


function changeFinish(){
  var nextStep = document.getElementById("wizard-next");
  if(nextStep!=undefined && nextStep!=null){
    nextStep.value="结束";
  }
}

