  /*showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label'),'$services.localization.render('core.copy.submit')')*/
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(0),'$services.localization.render('userPreferences.displayHidden.help.hint')')
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(1),'$services.localization.render('userPreferences.accessibility.help.hint')')
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(2),'$services.localization.render('userPreferences.timezone.help.hint')')
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(3),'$services.localization.render('userPreferences.editor.help.hint')')
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(4),'$services.localization.render('userPreferences.usertype.help.hint')')
    showIconhelpInfo(jq('#user-page-content #preferencesPane .xform dl dt label').eq(5),'$services.localization.render('userPreferences.extensionConflictSetup.help.hint')')
  function showIconhelpInfo(e,info) {
            e.hover(function(event){
                var target = jq(event.target);
                target.append("<div id=\"c7n-icon-help-info\" class=\"icon-hint-info icon-info-frequency\" style='left: "+(target.width()-33)+"px;'>\n" +
                    "    <div class=\"arrow\"></div>\n" +
                    "    <div class=\"icon-info-content \">"+info+"</div>\n" +
                    "</div>")
            },function(){
                jq("#c7n-icon-help-info").remove();
            });
        }
});

