require(['jquery'], function($) {
  $(".location-action-pick").click(function(){
    setTimeout(function() {
      if($(".createbody .modal .modal-body .xtree-finder")) {
        $(".createbody .modal .modal-body .xtree-finder").attr('placeholder',"搜索...");
      }
      var curruser = '$xcontext.getUser()';
      console.log(curruser.split('.')[1]);
      var currentId = "document:xwiki:" + curruser.split('.')[1] + ".WebHome";
      var list = $(".createbody .modal .modal-body .location-tree>ul>li>ul>li");
      $.each(list, function(i, val){
        if ($($(val).children()[1]).text() === '我的空间' && $(val).attr("id") !== currentId) {
           val.style.display = 'none';
        }
      });
    }, 1000);
  });
  $(".createbody .location-picker .modal").removeClass("fade");
  $(".createbody .modal .modal-footer .btn-primary").text("确定");
  $(".createbody .modal .modal-footer .btn-default").text("取消");
  $('.modal ').on('hide.bs.modal', function () {
    $("#body").css("overflow","auto");
  })
  $('.modal ').on('show.bs.modal', function () {
    $("#body").css("overflow","");
  })
})

require(['jquery'], function($) {
  $("#edit #editPanels > div.large.panel.expanded.DocumentInformation > div > dl > dt:nth-child(1) > label").text('页面语法:');
  
    $(document).unbind('keydown').bind('keydown', function(e){ 
		 var keyCode = e.keyCode || e.which || e.charCode;
        var ctrlKey = e.ctrlKey || e.metaKey;
        if(ctrlKey && keyCode == 83) {
          if ($("input[name='action_saveandcontinue']")) {
          $("input[name='action_saveandcontinue']").click();
        }
          return false;
        }
	});
});

require(['jquery', "$!services.webjars.url('org.xwiki.platform:xwiki-platform-tree-webjar', 'require-config.min.js', {'evaluate': true})"], function($) {
   require(['tree'], function($) {
     $(function () {
       //jstree树状浏览器监听，当hover节点是触发的事件
      $(".xtree").on("hover_node.jstree", function(e, node){
        if(node && node.node && node.node.a_attr && node.node.a_attr.id) {
          var id = node.node.a_attr.id;
          var a_element = $("[id='"+id+"']","#leftPanels");
          if(a_element) {
            a_element.attr("title", node.node.text);
          }
        }
      });
    });
     
    $('.xtree').on("changed.jstree", function (e, data) {
       var a = document.querySelector("#leftPanels .jstree-clicked");
       if (a) {
           a.scrollIntoView();
       }
    });
     
  });
});

