window.onload=function (){
var officeCollapseIcon1 = `<span style="margin-right: 9px;margin-top: -6px;" class="icon icon-export_PDF"></span>`;
var officeCollapseIcon2 = `<span style="margin-right: 9px;margin-top: -6px;" class="icon icon-export_EOT"></span>`;
var officeCollapseIcon3 = `<span style="margin-right: 9px;margin-top: -6px;" class="icon icon-export_RTF"></span>`;
var officeCollapse= document.getElementsByClassName("officeCollapse");
for(var i=0;i<officeCollapse.length;i++){
  if(i == 0){
     officeCollapse[i].insertAdjacentHTML('afterBegin', officeCollapseIcon1);
  } else if(i == 1) {
     officeCollapse[i].insertAdjacentHTML('afterBegin', officeCollapseIcon2);
  } else if(i == 2) {
     officeCollapse[i].insertAdjacentHTML('afterBegin', officeCollapseIcon3);
  }

}

var otherCollapseIcon1 = `<span style="margin-right: 9px;margin-top: -6px;" class="icon icon-export_html"></span>`;
var otherCollapseIcon2 = `<span style="margin-right: 9px;margin-top: -6px;" class="icon icon-export_XAR"></span>`;
