var handle;
var divTip;
define('toastMsg', {
  toast:function(msg){
        divTip = document.createElement("div");
    divTip.id = "c7n-tip";
    divTip.innerHTML = "<span>"+
        "            <div style=\"padding: 8px;text-align: center;\">"+
        "                <div style=\"padding: 10px 16px;border-radius: 2px;box-shadow: 0 4px 12px rgba(0,0,0,.12);background: #fff;display: inline-block;\">"+
        "                    <div >"+
        "                        <i class=\"icon icon-info\" style=\"color: #3367d6;\"></i>"+
        "                        <span>" + msg+ "</span>"+
        "                    </div>"+
        "                </div>"+
        "            </div>"+
        "        </span>";
    divTip.style.bottom = '40px';
    divTip.style.left = '0px';
    divTip.style.position = 'fixed';
    divTip.style = 'position: absolute;overflow: hidden;display: none;left: 24px;right: auto;top: auto;bottom: 24px;position: fixed;font-size: 13px;z-index:99999;';
    document.body.appendChild(divTip);
    var obj = document.getElementById("c7n-tip");
    obj.style.display = "block";
    handle = setInterval(show, 40);
  }
});

function show() {
    var obj = document.all ? document.all["c7n-tip"] : document.getElementById("c7n-tip");
    if(obj != null){
      if (parseInt(obj.style.left) > 100)
          clearInterval(handle);
      else
          obj.style.left = (parseInt(obj.style.left) + 8).toString() + "px";
    }
    window.setTimeout(hide,5000);
}

function hide(){
     var obj = document.all ? document.all["c7n-tip"] : document.getElementById("c7n-tip");
    if(obj != null){
        if (parseInt(obj.style.left) < 8) {
            clearInterval(handle);
            obj.style.display = "none";
        }
        else
            obj.style.left = (parseInt(obj.style.left) - 8).toString() + "px";
        document.body.removeChild(document.getElementById('c7n-tip'));
    }
}

require(['jquery'], function($) {
  $("#headerspacesearchinput").keydown(function(event){
       if(event.keyCode==13){
        searchPage();
    }
})

function searchPage() {
        var spaceName = $.trim($('#headerspacesearchinput').val());
        if(spaceName.length > 0){
            $('.has-children .space_down').css("display","none");
            $('.has-children .space_right').css("display","none");
            $('.has-children').addClass("displayUl");
            $('.has-children').removeClass("hiddenUl");
            $('.all-space-ul .space-wrap').css("display","none")
            recursiveChild($(".all-space-ul"), spaceName);
        }else {
            $('.has-children .space_down').css("display","none");
            $('.has-children .space_right').css("display","");
            $('.has-children').addClass("hiddenUl");
            $('.has-children').removeClass("displayUl");
            $('.all-space-ul .space-wrap').css("display","")
        }
        $('#recent-space-content').css("display","none")
        $('#all-space-content').css("display","block")
}

var i = 0;
function recursiveChild(a,targetName) {
       var arrs = a.children('li');
       var flag = false;
        if($(a).children('li') != null && arrs.length > 0){
            for (var j = 0; j < arrs.length; j++) {
               var currObj = $(arrs[j]);
                var obj = $(currObj).children('div');
                var name = obj.children('a.spaceName').attr("title");
                if ($(currObj).hasClass("has-children")){
                    if(currObj.children("ul")!=null && currObj.children("ul").length > 0){
                      var flag_child = recursiveChild($(currObj).children("ul"),targetName); 
                      if(flag == false){
                        flag = flag_child;
                      }
                    }
                if (flag_child){
                    currObj.children('div').css("display",'');
                    currObj.children('div').find('.space_down').css('display','')
                    
                }
                }
                if (name.toLowerCase().indexOf(targetName.toLowerCase()) != -1) {
                    obj.css("display",'')
                    flag =true
                }
            }
          
        }
          return flag;
  
     
    

}
})

