require(['jquery'], function($) {

function getCookie(c_name)
{
if (document.cookie.length>0)
  {
  c_start=document.cookie.indexOf(c_name + "=")
  if (c_start!=-1)
    { 
    c_start=c_start + c_name.length+1 
    c_end=document.cookie.indexOf(";",c_start)
    if (c_end==-1) c_end=document.cookie.length
    return unescape(document.cookie.substring(c_start,c_end))
    } 
  }
return ""
}

var wikiexternallink = document.getElementsByClassName("wikiexternallink");
if(wikiexternallink && wikiexternallink .length > 0){
  for (var i=0;i<wikiexternallink.length;i++) {
    (function(i){
     var issueid = wikiexternallink[i].getElementsByTagName("a")[0].dataset.issueid;
     var organizationid = wikiexternallink[i].getElementsByTagName("a")[0].dataset.organizationid;
     var projectid = wikiexternallink[i].getElementsByTagName("a")[0].dataset.projectid;
     var statusTag = wikiexternallink[i].getElementsByClassName("c7n-statusTag");

     if (issueid != null && organizationid != null && projectid != null) {
     var urlPrex = '$xcontext.getRequest().getHttpServletRequest().getSession().getAttribute("choerodon.request.api.url")';
     var searchUrl = urlPrex+"/agile/v1/projects/"+projectid+"/issues/"+issueid+"?organizationId="+organizationid;
     var token = 'bearer ' + getCookie('accessToken');
       $.ajax({
        headers: {
            'Authorization': token
        },
        type: "GET",
        url: searchUrl,
        success: function (data) {
          var name = data.statusMapDTO.name;
          var type = data.statusMapDTO.type;
          var statusTag = wikiexternallink[i].getElementsByClassName("c7n-statusTag");
          
          if (statusTag && name != null) {
              statusTag[0].innerText = name;
              switch(type)
             { 
                case "todo":
                  statusTag[0].style.backgroundColor = "#ffb100";
                  break;
                case "doing":
                  statusTag[0].style.backgroundColor = "#4d90fe";
                  break;
                case "done":
                  statusTag[0].style.backgroundColor = "#00bfa5";
                  break;
                default:
                  statusTag[0].style.backgroundColor = "#ffb100";
                  break;
             }
          }
        },
        error: function (err) {
           require(['toastMsg'],function(toastMsg){
                toastMsg.toast('获取issue失败！');
         })
        }
    });
   }
   })(i);
  }
}
});

