function getCollectFolder(){
    //获取所有收藏数据
  var allSpaceDiv = document.getElementById("allcollectArea");
  if(allSpaceDiv) {
    allSpaceDiv.innerHtml = circle;
        jQuery.ajax({
        type: "POST",
        contentType: "application/json; charset=utf-8",
        dataType: "json",
        data:JSON.stringify({
          user:'$xcontext.getUser()',
        }),
        url: "/collect/page?folderType=selectFolderList",
        async: "true",
        success: function (data) {
           var html=[{id:0,name:"我的收藏"}].concat(data).map(function(folder){
              return `<li class="collectName" onclick="selectFolder(event)" data-id="${folder.id}">${folder.name}</li>`
           }).join('');
           jQuery("#allcollectArea").html(html)

        }
    });
  }
}
function selectFolder(e){
  
  jQuery('#allcollectArea .collectName').removeClass("selected");
  jQuery(e.target).addClass("selected")
  selected=e.target.getAttribute("data-id");
}
function unSelectFolder(){
  jQuery('#allcollectArea .collectName.selected').removeClass("selected"); 
   selected=null;
}
function collect(e){
   if(!selected){
     require(['toastMsg'],function(toastMsg){
           toastMsg.toast('请选择收藏夹！');
        })
     return;
   }
   require(['jquery'], function () {
       var curruser = '$xcontext.getUser()';
       var currpage = jQuery("#currentPage").text().split(":")[1];
       var collect_id = selected;     
       var d = {
		"user":curruser,
		"folderId":collect_id,
         page:currpage,
	 }       
   
     var url=collect_id==='0'?"/collect/page?type=insertMyFolder": "/collect/page?type=insert";
      jQuery.ajax({
       type: "POST",
       contentType: "application/json; charset=utf-8",
      data: JSON.stringify(d),
      dataType: "json",
       url,
       success: function(data){
          require(['toastMsg'],function(toastMsg){
           if(data=='0'){
             toastMsg.toast('收藏失败！');
           }else{
            jQuery("#page-favourite-icon").attr("class","icon icon-star");
           jQuery("#page-favourite").text("取消收藏");
            jQuery("#btn-page-collect").attr("title","取消收藏");
            jQuery("#page-favourite").addClass("collect");
             cancelMenuPop();
             toastMsg.toast('收藏成功！');
             unSelectFolder();            
           }
        })
       },
      error: function (err) {
        require(['toastMsg'],function(toastMsg){
           toastMsg.toast('收藏失败！');
        })
      }
     });    
    });
}
require(['jquery'], function($) {
  function searchCollect() {
        var collectName = $.trim($('#headercollectsearchinput').val());
        if(collectName.length > 0){
            $('#allcollectArea .collectName').each(function(){            
              if($(this).text().indexOf(collectName)!==-1){
                $(this).css('display','block')
              }else{
                $(this).css('display','none')
              }
        });     
        }else {
           $('#allcollectArea .collectName').each(function(){
            $(this).css('display','block')
        });
        }       
}
  $("#headercollectsearchinput").keydown(function(event){
       if(event.keyCode==13){
        searchCollect();
    }
})
})

    function closeModal(ModalId) {
      jQuery("#" + ModalId + " .ant-modal-mask").addClass("ant-modal-mask-hidden");
      jQuery("#" + ModalId + " .ant-modal-wrap").css('display', 'none')
    }
    function openModal(ModalId) {
      jQuery("#" + ModalId + " .ant-modal-mask").removeClass("ant-modal-mask-hidden");
      jQuery("#" + ModalId + " .ant-modal-wrap").css('display', 'block')
    }
    function createCollectFolder() {
      require(['jquery'], function ($) {
        var values = $('#create-collect-pop-form').serializeObject();
        var parent = $('#create-collect-pop-form input[name=collect_name]').parent();
        // 有异常时不提交
        if (parent.find(".ant-form-has-error").size()>0) {
          return;
        }
        if (!values.collect_name || values.collect_name === '') {
          $('#create-collect-pop-form input[name=collect_name]').addClass("has-error");          
          parent.find(".ant-form-has-error").remove();
          parent.append("<div class='ant-form-has-error'>" + '请输入收藏夹名称!' + "</div>");
          return;
        }
        var curruser = '$xcontext.getUser()';
        var collect_name = values.collect_name;
        if (collect_name === "我的收藏") {
          require(['toastMsg'], function (toastMsg) {
            toastMsg.toast('存在同名收藏夹！');
          })
          return;
        }
        var d = {
          "user": curruser,
          "name": collect_name
        }
        jQuery.ajax({
          type: "POST",
          contentType: "application/json; charset=utf-8",
          data: JSON.stringify(d),
          dataType: "json",
          url: "/collect/page?folderType=insert",
          success: function (data) {
            require(['toastMsg'], function (toastMsg) {
              if (data == '0') {
                toastMsg.toast('创建失败！');
              } else {
                jQuery('#create-collect-pop-form .ant-input').val("");
                jQuery('#create-collect-pop-form input[name=collect_name]').parent().removeClass("ant-input-has-value");
                closeModal("create-collect-pop")
                getCollectFolder();
                toastMsg.toast('创建成功！');
              }
            })
          },
          error: function (err) {
            require(['toastMsg'], function (toastMsg) {
              toastMsg.toast('创建失败！');
            })
          }
        });
      })
    }

    require(['jquery'], function ($) {
      function checkName(name, callback) {
        var d = {
          "user": '$xcontext.getUser()',
          "name": name
        }
        jQuery.ajax({
          type: "POST",
          contentType: "application/json; charset=utf-8",
          data: JSON.stringify(d),
          dataType: "json",
          url: "/collect/page?folderType=check",
          success: function (data) {
            require(['toastMsg'], function (toastMsg) {
              if (data == '1') {
                callback(true)
              } else {
                callback(false)
              }
            })
          },
          error: function (err) {
            callback(false)
          }
        });
      }
      jQuery.prototype.serializeObject = function () {
        var obj = new Object();
        $.each(this.serializeArray(), function (index, param) {
          if (!(param.name in obj)) {
            obj[param.name] = param.value;
          }
        });
        return obj;
      };
      // 点击关闭
      $("#create-collect-pop .ant-modal-wrap").on('click', function () {
        jQuery('#create-collect-pop-form .ant-input').val("");
        jQuery('#create-collect-pop-form input[name=collect_name]').parent().removeClass("ant-input-has-value");
         jQuery('#create-collect-pop-form input[name=collect_name]').parent().find(".ant-form-has-error").remove();
        closeModal("create-collect-pop")
      })
      //点击content防止冒泡
      $("#create-collect-pop .ant-modal-content").on('click', function (e) {
        e.stopPropagation();
      })
      //点击取消，关闭
      $("#create-collect-pop .ant-modal-footer .btn-close").on('click', function (e) {
        jQuery('#create-collect-pop-form .ant-input').val("");
        jQuery('#create-collect-pop-form input[name=collect_name]').parent().removeClass("ant-input-has-value");
        jQuery('#create-collect-pop-form input[name=collect_name]').parent().find(".ant-form-has-error").remove();
        //清空form
        $('#create-collect-pop-form')[0].reset();
        closeModal("create-collect-pop")
      })
      $("#create-collect-pop input").on("focus", function () {
        $(this).parent().addClass("ant-input-focus");
      })
      $("#create-collect-pop input").on("blur", function () {
        $(this).parent().removeClass("ant-input-focus");
      })

      //解决中文输入法问题
      var flag = true;
      $('#create-collect-pop input').on('compositionstart', function () {
        flag = false;
      })
      $('#create-collect-pop input').on('compositionend', function () {
        flag = true;
      })
      //值改变时，清除异常状态
      $('#create-collect-pop input').on('input', function () {
        var _this = this;
        setTimeout(function () {
          if (flag) {
            var pattern = new RegExp("[`~!@#$^&*()=|{}':;',\\[\\].<>/?~！@#￥……&*（）——|{}【】‘；：”“'。，、？]", 'g');
            $(_this).val($(_this).val().replace(pattern, ''));
            var parent = $(_this).parent();
            if ($(_this).val() === '') {
              parent.removeClass("ant-input-has-value");
            } else {
              parent.addClass("ant-input-has-value");
            }
            var maxlength = $(_this).attr('maxlength');
            if (maxlength) {
              $(_this).next('.ant-input-icon').find('.ant-input-length-info').text($(_this).val().length + '/' + maxlength)
            }
            $(_this).removeClass("has-error");    
            parent.find(".ant-form-has-error").remove();
            checkName($(_this).val(), function (repeat) {
              if (repeat) {
                parent.append("<div class='ant-form-has-error'>" + '收藏夹名称重复!' + "</div>");
              }
            });
          }
        }, 0)
      })
    });

function editCollectFolder() {
      require(['jquery'], function () {
        var oldFolderName = getParams(location.search).folderName;
        var parent = jQuery('#edit-collect-pop-form input[name=collect_name]').parent();
        // 有异常时不提交
        if (parent.find(".ant-form-has-error").size()>0) {
          return;
        }
        var values = jQuery('#edit-collect-pop-form').serializeObject();
        // 不改变时，不请求
        if(oldFolderName===values.collect_name){
          closeModal("edit-collect-pop");
          return;
        }
        if (!values.collect_name || values.collect_name === '') {
          jQuery('#edit-collect-pop-form input[name=collect_name]').addClass("has-error");          
          parent.find(".ant-form-has-error").remove();
          parent.append("<div class='ant-form-has-error'>" + '请输入收藏夹名称!' + "</div>");
          return;
        }
        var curruser = '$xcontext.getUser()';
        var collect_name = values.collect_name;
        var folderId = getParams(location.search).folderId;
        if (collect_name === "我的收藏") {
          require(['toastMsg'], function (toastMsg) {
            toastMsg.toast('存在同名收藏夹！');
          })
          return;
        }
        var d = {
          "user": curruser,
          id: folderId,
          "name": collect_name
        }
        console.log(d);
        jQuery.ajax({
          type: "POST",
          contentType: "application/json; charset=utf-8",
          data: JSON.stringify(d),
          dataType: "json",
          url: "/collect/page?folderType=renameFolder",
          success: function (data) {
            require(['toastMsg'], function (toastMsg) {
              if (data == '0') {
                toastMsg.toast('修改失败！');
              } else {
                closeModal("edit-collect-pop")
                toastMsg.toast('修改成功！');
                window.location.href = '/bin/view/Main/收藏夹/?folderId=' + folderId + '&folderName=' + collect_name;
              }
            })
          },
          error: function (err) {
            require(['toastMsg'], function (toastMsg) {
              toastMsg.toast('修改失败！');
            })
          }
        });
        console.log('edit', values);
      })
    }

