      jQuery('#edit-collect-pop input').on('input', function () {
        var _this = this;
        setTimeout(function () {
          if (flag) {
            var pattern = new RegExp("[`~!@#$^&*()=|{}':;',\\[\\].<>/?~！@#￥……&*（）——|{}【】‘；：”“'。，、？]", 'g');
            jQuery(_this).val(jQuery(_this).val().replace(pattern, ''));
            var parent = jQuery(_this).parent();
            if (jQuery(_this).val() === '') {
              parent.removeClass("ant-input-has-value");
            } else {
              parent.addClass("ant-input-has-value");
            }
            var maxlength = jQuery(_this).attr('maxlength');
            if (maxlength) {
              jQuery(_this).next('.ant-input-icon').find('.ant-input-length-info').text(jQuery(_this).val().length + '/' + maxlength)
            }
            jQuery(_this).removeClass("has-error");
            var parent = jQuery(_this).parent();
            parent.find(".ant-form-has-error").remove();
            var folderName = getParams(location.search).folderName;
            if (folderName !== jQuery(_this).val()) {
              checkName(jQuery(_this).val(), function (repeat) {
                if (repeat||jQuery(_this).val()==="我的收藏") {
                  parent.append("<div class='ant-form-has-error'>" + '收藏夹名称重复!' + "</div>");
                }
              });
            }
          }
        }, 0)
      })
    });

require(['jquery'], function () {
  var isArticalPage = document.getElementById('pace-content');
  var scrollElement = document.documentElement;
  // 距离顶部100px再显示
  if (isArticalPage) {
    if (shouldShowToTop()) {
      jQuery("#to-top").css('display', 'block')
    } else {
      jQuery("#to-top").css('display', 'none')
    }
    window.addEventListener('scroll', handleScroll);
    jQuery("#to-top").on('click', toTop)
  } else {
    jQuery("#to-top").css('display', 'none');
  }
  const easeInOutCubic = function (t, b, c, d) {
    const cc = c - b;
    t /= d / 2;
    if (t < 1) {
      return cc / 2 * t * t * t + b;
    } else {
      return cc / 2 * ((t -= 2) * t * t + 2) + b;
    }
  };
  function shouldShowToTop() {
    return scrollElement.scrollTop >= 100;
  }
  function toTop() {
    const scrollTop = scrollElement.scrollTop;
    const startTime = Date.now();
    const frameFunc = function () {
      const timestamp = Date.now();
      const time = timestamp - startTime;
      scrollElement.scrollTop = easeInOutCubic(time, scrollTop, 0, 450);
      if (time < 450) {
        requestAnimationFrame(frameFunc);
      }
    };
    requestAnimationFrame(frameFunc);
  }
  function handleScroll() {
    if (shouldShowToTop()) {
      jQuery("#to-top").css('display', 'block')
    } else {
      jQuery("#to-top").css('display', 'none')
    }
  }
})

    function setSelect(fn) {
      // jQuery(function ($) {
        // /////
        // MAD-SELECT
        var madSelectHover = 0;
        jQuery(".mad-select").each(function () {
          var $input = jQuery(this).find("input"),
            $ul = jQuery(this).find("> ul"),
            $ulDrop = $ul.clone().addClass("mad-select-drop");
          jQuery(this)
            .append(
              // '<i class="material-icons">arrow_drop_down</i>',
               $ulDrop)
            .on({
              hover: function () { madSelectHover ^= 1; },
              click: function () { $ulDrop.toggleClass("show"); }
            });
          // PRESELECT
          $ul.add($ulDrop).find("li[data-value='" + $input.val() + "']").addClass("selected");
          // MAKE SELECTED
          $ulDrop.on("click", "li", function (evt) {
            evt.stopPropagation();
            
            $input.val(jQuery(this).data("value")); // Update hidden input value     
            if (fn) {
              fn(jQuery(this).data("value"))
            }
            $ul.find("li").eq(jQuery(this).index()).add(this).addClass("selected")
              .siblings("li").removeClass("selected");
          });
          // UPDATE LIST SCROLL POSITION
          $ul.on("click", function () {
            var liTop = $ulDrop.find("li.selected").position().top;
            $ulDrop.scrollTop(liTop + $ulDrop[0].scrollTop);
          });
        });
        // CLOSE ALL OPNED
        jQuery(document).on("mouseup", function () {
          if (!madSelectHover) jQuery(".mad-select-drop").removeClass("show");
        });
      // });
    }
    function Pagination(element, options) {
      this.current = 1;
      this.total = 0;
      this.pageSize = 50;
      this.totalPage =Math.floor(this.total / this.pageSize) + 1;
      this.init(element, options)
      this.handleChange = function (values) {
        if (options.onChange) {
          options.onChange({
            current: this.current,
            total: this.total,
            pageSize: this.pageSize
          })
        }
      }
    }
    Pagination.prototype = {
      init: function (element, options) {
        this.setPagination(options)
        this.element = element;
        this.render()
      },
      render: function () {
        var that = this;
        var current = this.current;
        var total = this.total;
        var pageSize = this.pageSize;
        var start = pageSize * (current - 1) + 1
        var end = Math.min(start + pageSize - 1, total);
        const startDisabled = current === 1;
        const endDisabled = current >= this.totalPage;
        var root = jQuery('<div class="Pagination"></div>');

        var select = jQuery('<div class="mad-select"><ul><li data-value="10">10</li><li data-value="30">30</li><li data-value="50">50</li></ul><input type="hidden" name="myOptions" value="10"></div>');
        var perPageHtml = jQuery('<div class="Pagination-pageControll"><span class="Pagination-options-label">每页行数</span></div>');
        perPageHtml.append(select);
        var calculateHtml = jQuery('<div class="calculate">' + start + '-' + end + '/' + total + '</div>');
        var operateHtml = jQuery(`<div><button class="start ant-btn-circle" ${startDisabled && 'disabled'}><span class="icon icon-first_page"></span></button><button class="pre ant-btn-circle" ${startDisabled && 'disabled'}><span class="icon icon-navigate_before"></span></button> <button class="next ant-btn-circle" ${endDisabled && 'disabled'}><span class="icon icon-navigate_next"></span></button><button class="end ant-btn-circle" ${endDisabled && 'disabled'}><span class="icon icon-last_page"></span></button></div>`);

        root.append(perPageHtml)
        root.append(calculateHtml)
        root.append(operateHtml)
        jQuery(this.element).html(root)
        
        select.find('input').val(pageSize)
        setSelect(function (pageSize) {
          that.setPagination({ pageSize: Number(pageSize) });
          that.handleChange()
        });
operateHtml.find('.pre').on('click', function (e) {
          e.stopPropagation();
          that.pre()
        })
        operateHtml.find('.next').on('click', function (e) {
          e.stopPropagation();
          that.next()
        })
        operateHtml.find('.start').on('click', function (e) {
          e.stopPropagation();
          that.toStart()
        })
        operateHtml.find('.end').on('click', function (e) {
          e.stopPropagation();
          that.toEnd()
        })
      },
      setPagination: function (options) {
        //console.log(options)
        var total = options.total!=undefined? options.total: this.total;
        var current = options.current!=undefined?options.current : this.current
        var pageSize = options.pageSize!=undefined?options.pageSize : this.pageSize
        var totalPage = Math.ceil(total / pageSize)||0;
        var start = pageSize * (current - 1) + 1;
        const newCurrent = Math.floor((total - 1) / pageSize) + 1;
        current = current > newCurrent ? newCurrent : current;
        this.setTotal(total)
        this.setPageSize(pageSize)
        this.setTotalPage(totalPage)
        this.setCurrent(current)
        this.render()
      },
      setCurrent: function (current) {
        if (current > 0 && current <= this.totalPage) {
          this.current = current;
        }
      },
      setTotal: function (total) {
        this.total = total;
      },
      setPageSize: function (pageSize) {
        this.pageSize = pageSize;
      },
      setTotalPage: function (totalPage) {
        this.totalPage = totalPage;
      },
      getCurrent: function () {
        return this.current;
      },
      getPagination: function () {
        return {
          current: this.current,
          total: this.total,
          pageSize: this.pageSize
        };
      },
      toPage: function (page) {
        //console.log('toPage' + page)
        this.setCurrent(page);
        this.render()
      },
      pre: function () {
        this.toPage(this.current - 1)
        this.handleChange()
      },
      next: function () {
        this.toPage(this.current + 1)
        this.handleChange()
      },
      toStart: function () {
        this.toPage(1)
        this.handleChange()
      },
      toEnd: function () {
        this.toPage(this.totalPage)
        this.handleChange()
      }
    }

require(['jquery'], function($) {

var spaceRename= document.getElementById("spaceRename");
var renameCancel = document.getElementById("rename-cancel-button");
var renameModalContainer = document.getElementById("rename-modal-container");

if (spaceRename) {
spaceRename.onclick = function () {
      if (renameModalContainer.style.display == "block") {
        renameModalContainer.style.display = "none";
      } else {
        renameModalContainer.style.display = "block";
      }
}
}
  
if (renameCancel) {
renameCancel.onclick = function () {
   renameModalContainer.style.display = "none";
}
}  
  
});

