<div class="xdialog-modal-container" id="rename-modal-container" style="text-align: center;display:none">
<div class="xdialog-screen" style="opacity: 0.5;"></div>
<div class="xdialog-box xdialog-box-confirmation" id="rename-content">
  <div id="rename-header">
    <div id="rename-title">空间重命名</div>
  </div>
  <div style="margin: 0 20px;">
    <input type="text" id="rename-input" value="$doc.title">
<div id="rename-button"><span id="rename-cancel-button">取消</span>
<span id="space-rename-button" onclick="spaceRename()">重命名</span>
</div>
</div>
</div>
<script type="text/javascript">
  function spaceRename() {
    var title= jQuery("#rename-input").val();
    var d = {
		"fullName":"$doc.getFullName().replaceAll('\\', '\\\\')",
		"title":title
	 }
    var options = {
      type: "PUT",
      contentType: "application/json; charset=utf-8",
      data: JSON.stringify(d),
      dataType: "json",
      url: "/rest/v1/page",
      success: function(data){
          if (data) {
            jQuery("#rename-modal-container").css("display","none");
            window.location.reload();
           }
       },
      error: function (err) {
        require(['toastMsg'],function(toastMsg){
           toastMsg.toast('修改空间名称出错！');
        })
      }
     };
    jQuery.ajax(options);
  }
</script>
{{/html}}
{{/velocity}}
